def draw_route(coordinates : list):
    turtle.pensize(2)
    turtle.speed(0)
    turtle.penup()
    turtle.setpos(0,200)

    print(len(coordinates), "list size")
    for i in range(0, len(coordinates)):
        turtle.goto(coordinates[i])
        turtle.pendown()
        turtle.hideturtle()
        

    turtle.done()

draw_route(coordinates)